mapper.select:
select

  distinct


from prod_common_remarks




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}

mapper.insert:
insert into prod_common_remarks (prod_common_remarks_id, `key`, company, 
  container_category, remarks_content, remarks_content_en, 
  is_del, create_time, create_user, 
  update_time, update_user, is_valid
  )
values (#{prodCommonRemarksId,jdbcType=INTEGER}, #{key,jdbcType=VARCHAR}, #{company,jdbcType=VARCHAR}, 
  #{containerCategory,jdbcType=VARCHAR}, #{remarksContent,jdbcType=VARCHAR}, #{remarksContentEn,jdbcType=VARCHAR}, 
  #{isDel,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{createUser,jdbcType=INTEGER}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=INTEGER}, #{isValid,jdbcType=INTEGER}
  )
